function formSend(){const e=document.querySelector(".gamers__form"),t=document.querySelector(".gamers__popup"),r=document.querySelector(".gamers__request");if(e){e.addEventListener("submit",(async function(n){n.preventDefault();let o=new FormData(n.target);fetch(n.target.action,{method:e.method,body:o,headers:{Accept:"application/json"}}).then((n=>{n.ok?(r.style.display="block",e.reset(),setTimeout((()=>{r.style.display="none"}),5e3)):n.json().then((e=>{Object.hasOwn(e,"errors")?(t.innerHTML=e.errors.map((e=>e.message)).join(", "),setTimeout((()=>{t.innerHTML=""}),5e3)):(t.innerHTML="Oops! There was a problem submitting your form",setTimeout((()=>{t.innerHTML=""}),5e3))}))})).catch((e=>{t.innerHTML="Oops! There was a problem submitting your form",setTimeout((()=>{t.innerHTML=""}),5e3)}))}))}}formSend();